async function httpGet (url) {

	return new Promise((resolve, reject) => {
		
		http.get(url, (res) => {

			const { statusCode } = res;
			const contentType = res.headers['content-type'];

			// res.setEncoding('utf8');
			const chunks = [];
			res.on('data', (chunk) => { chunks.push(chunk); });
			res.on('end', () => {
				
				const rawData = Buffer.concat(chunks).toString();
				
				if (statusCode !== 200) {
					
					const e = {status: statusCode, message: `Request Failed (${statusCode}). URL: ${url}`};
					console.error(e.message);
					
					// consume response data to free up memory
					res.resume();

					if (statusCode === 302) {
						console.error('IP blocked.');
						process.exit();
					}
					reject(e);
				}

				resolve(rawData);
			});
			
		}).on('error', (e) => {
			reject(e);
		});

	});
};

async function sleep(milliseconds) {
	return new Promise(resolve => {
		setTimeout(() => {
			resolve();
		}, milliseconds);
	});
}

async function getFirstList (cat) {
	const url = `http://www.66law.cn${cat.uri}`;
	const html = await httpGet(url);
	const { window } = new JSDOM(html);
	const $ = jQuery(window);

	try {
		const totalPages = Number($('.m-page').find('a:last').prev('a').text());
		return totalPages;
	}
	catch (e) {
		console.warn(`${cat.label}: Failed to get totalPages, set totalPages to 1.`);
		return 1;
	}
}

async function getList (cat, currentPage) {
	const url = `http://www.66law.cn${cat.uri}` + ( currentPage > 1 ? `page_${currentPage}.aspx` : '');
	const html = await httpGet(url);
	const { window } = new JSDOM(html);
	const $ = jQuery(window);
	
	$('.cs-list2 .tj-news').each(async (index, el) => {
		
		const qa = new Qa();

		qa.q = $(el).find('.tl>a').text();
		qa.url = `http://www.66law.cn${$(el).find('.tl>a').attr('href')}`;
		qa.cat = cat.label;
		qa.tags = $('.u-weizhi>a:gt(1)').map((index, el) => $(el).text()).toArray();
		qa.source = '66law';

		try {
			await qa.save();
		}
		catch (e) {
			// console.error(e.message);
		}
	});
}

async function getDetail (qa) {	
	// console.log(`get ${qa.url}`);
	const html = await httpGet(qa.url);
	const { window } = new JSDOM(html);
	const $ = jQuery(window);
	const content = $('.det-nr').html();
	if (!content) {
		console.warn(`no content find in page: ${qa.url}`);
		return await qa.remove();
	}
	qa.a = content.replace(/^\s+/, '')
		.replace(/ style=".*?"| class=".*?"/g, '')
		.replace(/<\/?a.*?>/g, '')
		.replace(/<img.*?>/g, '')
		.replace(/<\/?span.*?>/g, '')
		.replace(/<p><strong>延伸阅读：<\/strong><\/p><ul>.*?<\/ul>/, '')
		.replace('，有问题欢迎咨询华律网律师', '');

	return qa.save();
}

async function asyncGetCat (cats, index = 0) {

	const cat = cats[index];

	console.log(` -- Cat ${index} ${cat.label} --`);
	
	try {
		const totalPages = await getFirstList(cat);
		cat.totalPages = totalPages;

		await asyncGetList(cat);
	}
	catch (e) {
		console.error(cat.label, e.message);
	}

	if (cats[index + 1]) {
		asyncGetCat(cats, index + 1);
	}
}

async function asyncGetList (cat, currentPage = 1) {
	try {
		await getList(cat, currentPage);
		// console.log(cat.label, currentPage);
	}
	catch (e) {
		console.error(e.message);
	}

	if (cat.totalPages === currentPage) {
		return;
	}
	else {
		await sleep(1000);
		return asyncGetList(cat, currentPage + 1);
	}
}

