const queue = [];

Qa.find({source: '66law', a: {$exists: false}}).cursor().on('data', qa => {
	queue.push(qa);
});

setTimeout(() => {
	getQueueDetail(queue);
}, 1000);

async function getQueueDetail(queue) {
	const qa = queue.shift();
	if (!qa) {
		process.exit();
	}
	try {
		await getDetail(qa);
	}
	catch (e) {
		console.error(e);
		// if (e.status === 403 || e.status === 404) {
		// 	await qa.remove();
		// 	console.log(`Removed ${qa.url}`);
		// }
	}
	await sleep(1000);
	getQueueDetail(queue);
}
